$.fn.vimeo = function (methd) {
    console.log(methd);
   if (methd == 'play') {
       $(this).trigger(methd, {currentTarget: $(this)[0]})
       setTimeout(function () {
           console.log('finish')
           $(this).trigger('finish', { currentTarget: $(this)[0] });
       }.bind(this), 5000);
   }
}

